import { useSelector } from "react-redux";

const Product = ({post}) => {


  const {cart} =useSelector

  return (
    <div>
      <div>
        <p>{post.title}</p>
      </div>
      <div>
        <p>{post.description}</p>
      </div>
      <div>
        <img src={post.image}/> 
      </div>
      <div>
        <p>{post.price}</p>
      </div>

    </div>
  );
};

export default Product;
